

$body = @{
    matric_number = "0200470001"
    attendance_code = "WEUNJO"
    latitude = 50
    longitude = 50
    image = "data:image/jpeg;base64,"
} | ConvertTo-Json

try {
    $response = Invoke-RestMethod -Uri "http://127.0.0.1:8005/api/student/capture-attendance" -Method Post -Body $body -ContentType "application/json" -ErrorAction Stop
    Write-Host "Success:"
    $response | Format-List
} catch {
    Write-Host "Error:"
    $_.Exception.Response | Format-List
    $stream = $_.Exception.Response.GetResponseStream()
    $reader = New-Object System.IO.StreamReader($stream)
    $reader.ReadToEnd()
}
